const onSignal: string = '';
const offSignal: string = '';

const decodeSignalFromHexToBinary = (signal: string): (0|1)[] => {
    let result: any= [];
    for (const hex of signal.split('')) {
        const bits = parseInt(hex, 16).toString(2).padStart(4, '0');
        result = result.concat(bits.split('').map(Number));
    }
    return result;
};

const on: (0|1)[] = decodeSignalFromHexToBinary(onSignal);
const off: (0|1)[] = decodeSignalFromHexToBinary(offSignal);

export { on, off };
